CREATE PROCEDURE [SONDA].[SONDA_SP_ADD_CONSIGNMENT_BY_XML](
	@XML XML
)
AS
BEGIN
	BEGIN TRY
		--
		DECLARE @CONSIGNMENT_HEADER TABLE(
			 [CUSTOMER_ID] VARCHAR(250)
			 ,[DATE_CREATE] DATETIME
			 ,[DATE_UPDATE] DATETIME
			 ,[STATUS] VARCHAR(250)
			 ,[POSTED_BY] VARCHAR(250)
			 ,[IS_POSTED] INT
			 ,[POS_TERMINAL] VARCHAR(250)
			 ,[GPS_URL] VARCHAR(250)
			 ,[DOC_DATE] DATETIME
			 ,[CLOSED_ROUTE_DATETIME] DATETIME
			 ,[IS_ACTIVE_ROUTE] INT
			 ,[DUE_DATE] DATETIME
			 ,[CONSIGNMENT_HH_NUM] INT
			 ,[TOTAL_AMOUNT] NUMERIC(18,6)
			 ,[DOC_SERIE] VARCHAR(250)
			 ,[DOC_NUM] INT
			 ,[IMG] VARCHAR(MAX)
			 ,[IS_CLOSED] INT
			 ,[CONSIGNMENT_TYPE] VARCHAR(250)
			 ,[IS_RECONSIGN] INT
			 ,[INVOICE_SERIE] VARCHAR(250)
			 ,[INVOICE_NUM] INT
		);
		--
		DECLARE @CONSIGNMENT_DETAIL TABLE(
			[CONSIGNMENT_ID] INT
			 ,[SKU] VARCHAR(250)
			 ,[LINE_NUM] INT
			 ,[QTY] INT
			 ,[PRICE] NUMERIC(18,6)
			 ,[DISCOUNT] NUMERIC(18,6)
			 ,[TOTAL_LINE] NUMERIC(18,6)
			 ,[POSTED_DATETIME] DATETIME
			 ,[PAYMENT_ID] INT
			 ,[HANDLE_SERIAL] INT
			 ,[SERIAL_NUMBER] VARCHAR(250)
		);
		--
		DECLARE @RESULTS TABLE(
			[DOC_SERIE] VARCHAR(250)
			,[DOC_NUM] INT
			,[CONSIGNMENT_ID] INT
			,[DUE_DATE] DATETIME
			,[IS_POSTED] INT
			,[POSTED_RESPONSE] VARCHAR(MAX)
		);
		--
		DECLARE @DEFAULT_WAREHOUSE VARCHAR(250), @ROUTE_ID VARCHAR(250), @DEVICE_ID VARCHAR(50);
		--
		SELECT
			@DEFAULT_WAREHOUSE = [x].[Rec].[query]('./default_warehouse').[value]('.' ,'varchar(250)')
			,@ROUTE_ID = [x].[Rec].[query]('./routeid').[value]('.' ,'varchar(250)')
			,@DEVICE_ID = [x].[Rec].[query]('./deviceId').[value]('.' ,'varchar(250)')
		FROM
			@XML.[nodes]('Data') AS [x] ([Rec]);

		-- --------------------------------------------------------------------------------------------
		-- Se valida el identificador del dispositivo
		-- --------------------------------------------------------------------------------------------
		EXEC [SONDA].[SONDA_SP_VALIDATE_DEVICE_ID_OF_USER_FOR_TRANSACTION] @CODE_ROUTE = @ROUTE_ID , -- varchar(50)
			@DEVICE_ID = @DEVICE_ID -- varchar(50)

		--
		INSERT INTO @CONSIGNMENT_HEADER
				(
					[CUSTOMER_ID]
					,[DATE_CREATE]
					,[DATE_UPDATE]
					,[STATUS]
					,[POSTED_BY]
					,[IS_POSTED]
					,[POS_TERMINAL]
					,[GPS_URL]
					,[DOC_DATE]
					,[CLOSED_ROUTE_DATETIME]
					,[IS_ACTIVE_ROUTE]
					,[DUE_DATE]
					,[CONSIGNMENT_HH_NUM]
					,[TOTAL_AMOUNT]
					,[DOC_SERIE]
					,[DOC_NUM]
					,[IMG]
					,[IS_CLOSED]
					,[CONSIGNMENT_TYPE]
					,[IS_RECONSIGN]
					,[INVOICE_SERIE]
					,[INVOICE_NUM]
				)
		SELECT
			[x].[Rec].[query]('./CustomerId').[value]('.' ,'varchar(250)')
			,[x].[Rec].[query]('./DateCreate').[value]('.' ,'datetime')
			,CASE [x].[Rec].[query]('./DateUpdate').[value]('.' ,'varchar(50)') WHEN 'NULL' THEN NULL WHEN 'UNDEFINED' THEN NULL ELSE [x].[Rec].[query]('./DateUpdate').[value]('.' ,'datetime') END
			,[x].[Rec].[query]('./Status').[value]('.' ,'varchar(250)')
			,[x].[Rec].[query]('./PostedBy').[value]('.' ,'varchar(250)')
			,1
			,[x].[Rec].[query]('./PosTerminal').[value]('.' ,'varchar(250)')
			,[x].[Rec].[query]('./GpsUrl').[value]('.' ,'varchar(250)')
			,CASE [x].[Rec].[query]('./DocDate').[value]('.' ,'varchar(250)') WHEN 'NULL' THEN NULL WHEN 'UNDEFINED' THEN NULL ELSE [x].[Rec].[query]('./DocDate').[value]('.' , 'datetime') END
			,CASE [x].[Rec].[query]('./ClosedRouteDatetime').[value]('.' ,'varchar(250)') WHEN 'NULL' THEN NULL WHEN 'UNDEFINED' THEN NULL ELSE [x].[Rec].[query]('./ClosedRouteDatetime').[value]('.' , 'datetime') END
			,CASE [x].[Rec].[query]('./IsActiveRoute').[value]('.' ,'varchar(250)') WHEN 'NULL' THEN 1 WHEN 'UNDEFINED' THEN 1 ELSE [x].[Rec].[query]('./IsActiveRoute').[value]('.' , 'int') END
			,CASE [x].[Rec].[query]('./DueDate').[value]('.' ,'varchar(250)') WHEN 'NULL' THEN DATEADD(DAY,30,GETDATE()) WHEN 'UNDEFINED' THEN DATEADD(DAY,30,GETDATE()) ELSE [x].[Rec].[query]('./DueDate').[value]('.' , 'datetime') END
			,[x].[Rec].[query]('./ConsignmentId').[value]('.' ,'int')
			,[x].[Rec].[query]('./TotalAmount').[value]('.' ,'numeric(18,6)')
			,[x].[Rec].[query]('./DocSerie').[value]('.' ,'varchar(250)')
			,[x].[Rec].[query]('./DocNum').[value]('.' ,'int')
			,[x].[Rec].[query]('./Image').[value]('.' ,'varchar(max)')
			,0
			,[x].[Rec].[query]('./ConsignmentType').[value]('.' ,'varchar(250)')
			,[x].[Rec].[query]('./IsReconsign').[value]('.' ,'int')
			,CASE [x].[Rec].[query]('./InvoiceSerie').[value]('.' ,'varchar(250)') WHEN 'NULL' THEN NULL WHEN 'UNDEFINED' THEN NULL ELSE [x].[Rec].[query]('./InvoiceSerie').[value]('.' , 'varchar(250)') END
			,CASE [x].[Rec].[query]('./InvoiceNum').[value]('.' ,'varchar(250)') WHEN 'NULL' THEN NULL WHEN 'UNDEFINED' THEN NULL ELSE [x].[Rec].[query]('./InvoiceNum').[value]('.' , 'int') END
		FROM
			@XML.[nodes]('Data/consignment') AS [x] ([Rec]); 

	--
	INSERT INTO @CONSIGNMENT_DETAIL
			(
				[CONSIGNMENT_ID]
				,[SKU]
				,[LINE_NUM]
				,[QTY]
				,[PRICE]
				,[DISCOUNT]
				,[TOTAL_LINE]
				,[POSTED_DATETIME]
				,[PAYMENT_ID]
				,[HANDLE_SERIAL]
				,[SERIAL_NUMBER]
			)
	SELECT [x].[Rec].[query]('./ConsignmentId').[value]('.' ,'int')
			,[x].[Rec].[query]('./Sku').[value]('.' ,'varchar(250)')
			,[x].[Rec].[query]('./LineNum').[value]('.' ,'int')
			,[x].[Rec].[query]('./Qty').[value]('.' ,'int')
			,[x].[Rec].[query]('./Price').[value]('.' ,'numeric(18,6)')
			,[x].[Rec].[query]('./Discount').[value]('.' ,'numeric(18,6)')
			,[x].[Rec].[query]('./TotalLine').[value]('.' ,'numeric(18,6)')
			,[x].[Rec].[query]('./PostedDateTime').[value]('.' ,'datetime')
			,CASE [x].[Rec].[query]('./PaymentId').[value]('.' ,'varchar(250)') WHEN 'NULL' THEN NULL WHEN 'UNDEFINED' THEN NULL ELSE [x].[Rec].[query]('./PaymentId').[value]('.' , 'int') END
			,[x].[Rec].[query]('./HandleSerial').[value]('.' ,'int')
			,CASE [x].[Rec].[query]('./HandleSerial').[value]('.' ,'varchar(250)') WHEN 'NULL' THEN NULL WHEN 'UNDEFINED' THEN NULL ELSE [x].[Rec].[query]('./SerialNumber').[value]('.' , 'varchar(250)') END
		FROM
			@XML.[nodes]('Data/consignment/ConsignmentRows') AS [x] ([Rec]); 
	--

	DECLARE @CONSIGNMENT_ID_BO INT
			,@CONSIGNMENT_ID_HH INT
			,@DOC_SERIE VARCHAR(250)
			,@DOC_NUM INT
			,@IS_RECONSIGN INT
			,@DUE_DATE_CONSIGNMENT DATETIME
			,@CODE_SKU VARCHAR(250)
			,@QTY_SKU INT
			,@HANDLE_SERIAL INT
			,@SERIAL_NUMBER VARCHAR(250)
			,@ERROR_MESSAGE VARCHAR(MAX)
			,@INVOICE_SERIE VARCHAR(250)
			,@INVOICE_NUM INT;
	--
	WHILE EXISTS(SELECT TOP 1 1 FROM @CONSIGNMENT_HEADER) BEGIN
		SELECT TOP 1 
			@CONSIGNMENT_ID_HH = [CH].[CONSIGNMENT_HH_NUM]
			,@DOC_SERIE = [CH].[DOC_SERIE]
			,@DOC_NUM = [CH].[DOC_NUM]
			,@DUE_DATE_CONSIGNMENT = [CH].[DUE_DATE]
			,@INVOICE_SERIE = [CH].[INVOICE_SERIE]
			,@INVOICE_NUM = [CH].[INVOICE_NUM]
		FROM @CONSIGNMENT_HEADER AS [CH]

		BEGIN TRY	
			--
			BEGIN TRAN [INSERT_CONSIGNMENT];
			
			--
			INSERT INTO [SONDA].[SWIFT_CONSIGNMENT_HEADER]
					(
						[CUSTOMER_ID]
						,[DATE_CREATE]
						,[DATE_UPDATE]
						,[STATUS]
						,[POSTED_BY]
						,[IS_POSTED]
						,[POS_TERMINAL]
						,[GPS_URL]
						,[DOC_DATE]
						,[CLOSED_ROUTE_DATETIME]
						,[IS_ACTIVE_ROUTE]
						,[DUE_DATE]
						,[CONSIGNMENT_HH_NUM]
						,[TOTAL_AMOUNT]
						,[DOC_SERIE]
						,[DOC_NUM]
						,[IMG]
						,[IS_CLOSED]
						,[CONSIGNMENT_TYPE]
					)
			SELECT 
				[CH].[CUSTOMER_ID]
				,[CH].[DATE_CREATE]
				,[CH].[DATE_UPDATE]
				,[CH].[STATUS]
				,[CH].[POSTED_BY]
				,[CH].[IS_POSTED]
				,[CH].[POS_TERMINAL]
				,[CH].[GPS_URL]
				,[CH].[DOC_DATE]
				,[CH].[CLOSED_ROUTE_DATETIME]
				,[CH].[IS_ACTIVE_ROUTE]
				,[CH].[DUE_DATE]
				,[CH].[CONSIGNMENT_HH_NUM]
				,[CH].[TOTAL_AMOUNT]
				,[CH].[DOC_SERIE]
				,[CH].[DOC_NUM]
				,[CH].[IMG]
				,[CH].[IS_CLOSED]
				,[CH].[CONSIGNMENT_TYPE]
			FROM @CONSIGNMENT_HEADER AS [CH]
			WHERE [CH].[DOC_SERIE] = @DOC_SERIE
			AND [CH].[DOC_NUM] = @DOC_NUM

			--
			SET @CONSIGNMENT_ID_BO = SCOPE_IDENTITY();

			--
			INSERT INTO [SONDA].[SWIFT_CONSIGNMENT_DETAIL]
					(
						[CONSIGNMENT_ID]
						,[SKU]
						,[LINE_NUM]
						,[QTY]
						,[PRICE]
						,[DISCOUNT]
						,[TOTAL_LINE]
						,[POSTED_DATETIME]
						,[PAYMENT_ID]
						,[HANDLE_SERIAL]
						,[SERIAL_NUMBER]
					)
			SELECT 
				@CONSIGNMENT_ID_BO
				,[CD].[SKU]
				,[CD].[LINE_NUM]
				,[CD].[QTY]
				,[CD].[PRICE]
				,[CD].[DISCOUNT]
				,[CD].[TOTAL_LINE]
				,[CD].[POSTED_DATETIME]
				,[CD].[PAYMENT_ID]
				,[CD].[HANDLE_SERIAL]
				,[CD].[SERIAL_NUMBER] 
			FROM @CONSIGNMENT_DETAIL AS [CD]
			WHERE [CD].[CONSIGNMENT_ID] = @CONSIGNMENT_ID_HH

			--
			IF(@INVOICE_SERIE IS NOT NULL AND @INVOICE_NUM IS NOT NULL) BEGIN
				UPDATE [SONDA].[SONDA_POS_INVOICE_HEADER] SET [CONSIGNMENT_ID] = @CONSIGNMENT_ID_BO WHERE [CDF_SERIE] = @INVOICE_SERIE AND [INVOICE_ID] = @INVOICE_NUM AND [POS_TERMINAL] = @ROUTE_ID
			END

			--
			IF(@IS_RECONSIGN <> 1) BEGIN
				WHILE EXISTS(SELECT TOP 1 1 FROM @CONSIGNMENT_DETAIL WHERE [CONSIGNMENT_ID] = @CONSIGNMENT_ID_HH) BEGIN
						SELECT TOP 1 
							@CODE_SKU = [CD].[SKU]
							,@QTY_SKU = [CD].[QTY]
							,@HANDLE_SERIAL = [CD].[HANDLE_SERIAL]
							,@SERIAL_NUMBER = [CD].[SERIAL_NUMBER]
						FROM @CONSIGNMENT_DETAIL AS [CD]
						WHERE [CD].[CONSIGNMENT_ID] = @CONSIGNMENT_ID_HH

						UPDATE [SONDA].[SONDA_POS_SKUS] SET [ON_HAND] = ([ON_HAND] - @QTY_SKU) WHERE [SKU] = @CODE_SKU AND [ROUTE_ID] = @DEFAULT_WAREHOUSE
						IF(@HANDLE_SERIAL = 1)BEGIN
							UPDATE [SONDA].[SWIFT_INVENTORY] SET [ON_HAND] = ([ON_HAND] - @QTY_SKU) WHERE [SKU] = @CODE_SKU AND [WAREHOUSE] = @DEFAULT_WAREHOUSE AND [SERIAL_NUMBER] = @SERIAL_NUMBER
						END
						ELSE BEGIN
							UPDATE [SONDA].[SWIFT_INVENTORY] SET [ON_HAND] = ([ON_HAND] - @QTY_SKU) WHERE [SKU] = @CODE_SKU AND [WAREHOUSE] = @DEFAULT_WAREHOUSE AND [SERIAL_NUMBER] IS NULL;
						END

						--
						DELETE FROM @CONSIGNMENT_DETAIL WHERE [SKU] = @CODE_SKU AND [SERIAL_NUMBER] = @SERIAL_NUMBER AND [QTY] = @QTY_SKU AND [CONSIGNMENT_ID] = @CONSIGNMENT_ID_HH
				END
			END
			--
			INSERT INTO @RESULTS
					(
						[DOC_SERIE]
						,[DOC_NUM]
						,[CONSIGNMENT_ID]
						,[DUE_DATE]
						,[IS_POSTED]
						,[POSTED_RESPONSE]
					)
			VALUES
					(
						@DOC_SERIE  -- DOC_SERIE - varchar(250)
						,@DOC_NUM  -- DOC_NUM - int
						,@CONSIGNMENT_ID_BO
						,@DUE_DATE_CONSIGNMENT  -- DUE_DATE - datetime
						,1  -- IS_POSTED - int
						,'PROCESO EXITOSO'  -- POSTED_RESPONSE - varchar(250)
					)

		--
		COMMIT TRANSACTION [INSERT_CONSIGNMENT];

		END TRY
		BEGIN CATCH
			--
			ROLLBACK TRANSACTION [INSERT_CONSIGNMENT];
			
			--
			SET @ERROR_MESSAGE = ERROR_MESSAGE();

			--
			INSERT INTO @RESULTS
					(
						[DOC_SERIE]
						,[DOC_NUM]
						,[CONSIGNMENT_ID]
						,[DUE_DATE]
						,[IS_POSTED]
						,[POSTED_RESPONSE]
					)
			VALUES
					(
						@DOC_SERIE  -- DOC_SERIE - varchar(250)
						,@DOC_NUM  -- DOC_NUM - int
						,@CONSIGNMENT_ID_HH
						,@DUE_DATE_CONSIGNMENT  -- DUE_DATE - datetime
						,0  -- IS_POSTED - int
						,@ERROR_MESSAGE  -- POSTED_RESPONSE - varchar(250)
					)
		END CATCH

		--
		DELETE FROM @CONSIGNMENT_HEADER WHERE [DOC_SERIE] = @DOC_SERIE AND [DOC_NUM] = @DOC_NUM

		--
		DELETE FROM @CONSIGNMENT_DETAIL WHERE [CONSIGNMENT_ID] = @CONSIGNMENT_ID_HH

	END

	--
	SELECT 
		[DOC_SERIE]
		,[DOC_NUM]
		,[CONSIGNMENT_ID]
		,[DUE_DATE]
		,[IS_POSTED]
		,[POSTED_RESPONSE] 
	FROM @RESULTS

	END TRY
	BEGIN CATCH
		--
		DECLARE @ERROR VARCHAR(MAX) = ERROR_MESSAGE();
		
		--
		RAISERROR(@ERROR,16,1);

	END CATCH
END
