-- Fecha de Creacion: 	8/2/2018 @ G-FORCE-TEAM Sprint G-FORCE@GATO
-- Description:			SP que actualiza la imagen del operador

/*
-- Ejemplo de Ejecucion:
				EXEC [SONDA].[SONDA_SP_UPDATE_USER_IMAGE]
					@CODE_ROUTE = '46'
					,@IMAGE = 'data:image/png;base64,'
				-- 
				SELECT * FROM SONDA.[USERS] WHERE [SELLER_ROUTE] = '46'
*/
-- =============================================
CREATE PROCEDURE [SONDA].[SONDA_SP_UPDATE_USER_IMAGE](
	@CODE_ROUTE VARCHAR(50)
	,@IMAGE VARCHAR(MAX)
)
AS
BEGIN
	BEGIN TRY
		UPDATE SONDA.[USERS]
		SET	
			[IMAGE] = @IMAGE
		WHERE [SELLER_ROUTE] = @CODE_ROUTE
		--
		SELECT  1 as Resultado , 'Proceso Exitoso' Mensaje ,  0 Codigo, '' DbData
	END TRY
	BEGIN CATCH
		SELECT  -1 as Resultado
		,ERROR_MESSAGE() Mensaje 
		,@@ERROR Codigo 
	END CATCH
END
