-- Autor:				rodrigo.gomez
-- Fecha de Creacion: 	7/4/2018 @ GFORCE-Team Sprint Elefante
-- Description:			Actualiza la foto de un usuario desde Sonda Super

/*
-- Ejemplo de Ejecucion:
				EXEC [SONDA].[SWIFT_SP_UPDATE_USER_PICTURE]
					@LOGIN = 'rudi@SONDA',
					@IMAGE = 'data:image/png;base64,'

				SELECT IMAGE FROM SONDA.USERS WHERE LOGIN = 'RUDI@SONDA'
*/
-- =============================================
CREATE PROCEDURE [SONDA].[SWIFT_SP_UPDATE_USER_PICTURE]
    (
     @LOGIN VARCHAR(50)
    ,@IMAGE VARCHAR(MAX)
    )
AS
BEGIN
    SET NOCOUNT ON;
	--
    BEGIN TRY
        UPDATE
            [SONDA].[USERS]
        SET
            [IMAGE] = @IMAGE
        WHERE
            [LOGIN] = @LOGIN;
		--
        SELECT
            1 AS [Resultado]
           ,'Proceso Exitoso' [Mensaje]
           ,0 [Codigo]
           ,'' [DbData];
    END TRY
    BEGIN CATCH
        SELECT
            -1 AS [Resultado]
           ,CASE CAST(@@ERROR AS VARCHAR)
              WHEN '2627' THEN ''
              ELSE ERROR_MESSAGE()
            END [Mensaje]
           ,@@ERROR [Codigo]; 
    END CATCH;
END;
